import "./App.css";
import io from "socket.io-client";
import { useState, useEffect } from "react";
import Header from "./components/header/header";
import { googleLogout, useGoogleLogin } from "@react-oauth/google";
import axios from "axios";

const socket = io(process.env.REACT_APP_BACKEND_URL, {
  withCredentials: true,
});
socket.on("connect", () => {
  console.log("Connected to server");
});

socket.on("disconnect", () => {
  console.log("Disconnected from server");
});

function App() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [matchStarted, setMatchStarted] = useState(null);
  const [result, setResult] = useState(null);
  const [playerMove, setPlayerMove] = useState(null);
  const login = useGoogleLogin({
    onSuccess: (codeResponse) => setUser(codeResponse),
    onError: (error) => console.log("Login Failed:", error),
  });

  useEffect(() => {
    if (user) {
      axios
        .get(
          `https://www.googleapis.com/oauth2/v1/userinfo?access_token=${user.access_token}`,
          {
            headers: {
              Authorization: `Bearer ${user.access_token}`,
              Accept: "application/json",
            },
          }
        )
        .then((res) => {
          setProfile(res.data);
        })
        .catch((err) => console.log(err));
    }
  }, [user]);

  useEffect(() => {
    socket.on("startMatch", () => {
      setMatchStarted(true);
      setResult(null);
    });

    socket.on("result", (res) => {
      setResult(res);
      setMatchStarted(false);
    });

    return () => {
      socket.off("startMatch");
      socket.off("gameResult");
    };
  }, []);

  // log out function to log the user out of google and set the profile array to null
  const logOut = () => {
    googleLogout();
    setProfile(null);
  };

  const createMatch = () => {
    const matchId = Math.random().toString(36).substring(7);
    socket.emit("createMatch", matchId);
    console.log(`Match created with ID: ${matchId}`);
  };

  const joinMatch = () => {
    const matchId = prompt("Enter Match ID:");
    socket.emit("joinMatch", matchId);
  };

  const handleMove = (move) => {
    setPlayerMove(move);
    socket.emit("playerMove", move);
  };

  return (
    <>
      <Header
        user={profile}
        login={login}
        logout={logOut}
        createMatch={createMatch}
        joinMatch={joinMatch}
      />
      {matchStarted ? (
        <>
          <button onClick={handleMove("Stone")}>Stone</button>
          <button onClick={handleMove("Scroll")}>Stone</button>
          <button onClick={handleMove("Sheers")}>Stone</button>
        </>
      ) : (
        <div>Game not on</div>
      )}
    </>
  );
}

export default App;
